    create table categoria (
        categoria_pai_id integer,
        id integer not null auto_increment,
        nome varchar(100) not null,
        primary key (id)
    ) engine=InnoDB;

    create table cliente (
        id integer not null auto_increment,
        cpf varchar(14) not null,
        nome varchar(100) not null,
        primary key (id)
    ) engine=InnoDB;

    create table cliente_contato (
        cliente_id integer not null,
        descricao varchar(255),
        tipo varchar(255) not null,
        primary key (cliente_id, tipo)
    ) engine=InnoDB;

    create table cliente_detalhe (
        cliente_id integer not null,
        data_nascimento date,
        sexo enum ('FEMININO','MASCULINO') not null,
        primary key (cliente_id)
    ) engine=InnoDB;

    create table estoque (
        id integer not null auto_increment,
        produto_id integer not null,
        quantidade integer,
        primary key (id)
    ) engine=InnoDB;

    create table item_pedido (
        pedido_id integer not null,
        preco_produto decimal(38,2) not null,
        produto_id integer not null,
        quantidade integer not null,
        primary key (pedido_id, produto_id)
    ) engine=InnoDB;

    create table nota_fiscal (
        pedido_id integer not null,
        data_emissao datetime(6) not null,
        xml tinyblob not null,
        primary key (pedido_id)
    ) engine=InnoDB;

    create table pagamento (
        data_vencimento date,
        pedido_id integer not null,
        status enum ('CANCELADO','PROCESSANDO','RECEBIDO') not null,
        tipo_pagamento varchar(31) not null,
        numero_cartao varchar(50),
        codigo_barras varchar(100),
        primary key (pedido_id)
    ) engine=InnoDB;

    create table pedido (
        cliente_id integer not null,
        estado varchar(2),
        id integer not null auto_increment,
        total decimal(38,2) not null,
        data_conclusao datetime(6),
        data_criacao datetime(6) not null,
        data_ultima_atualizacao datetime(6),
        cep varchar(9),
        numero varchar(10),
        status enum ('AGUARDANDO','CANCELADO','PAGO') not null,
        bairro varchar(50),
        cidade varchar(50),
        complemento varchar(50),
        logradouro varchar(100),
        primary key (id)
    ) engine=InnoDB;

    create table produto (
        id integer not null auto_increment,
        preco decimal(38,2),
        data_criacao datetime(6) not null,
        data_ultima_atualizacao datetime(6),
        nome varchar(100) not null,
        descricao tinytext,
        foto tinyblob,
        primary key (id)
    ) engine=InnoDB;

    create table produto_atributo (
        produto_id integer not null,
        nome varchar(100) not null,
        valor varchar(255)
    ) engine=InnoDB;

    create table produto_categoria (
        categoria_id integer not null,
        produto_id integer not null
    ) engine=InnoDB;

    create table produto_tag (
        produto_id integer not null,
        tag varchar(50) not null
    ) engine=InnoDB;

